  let sum: number = 0;
  for (let i = 0; i < expenses.length; i++) {
    sum += expenses[i].money;
    // console.log(typeof expenses[i].money);
    // console.log(sum);
    // console.log(expenses.length);
  }

  useEffect(() => {
    // sum = 0;
  }, [expenses]);

  return (
    <>
      <div className="BudgetTotal">
        총 지출: <span>{sum}</span>원
      </div>
    </>
  );
};

export default Calculator;
