import React from "react";
import AlertBanner from "../components/AlertBanner";
import BudgetCalculatorForm from "../components/BudgetCalculatorForm";
import DeleteList from "../components/DeleteList";
// import "./App.css";

function FormPage() {
  const alertMessage = (key: string) => {
    switch (key) {
      case "CREATE":
        return <p>아이템이 생성되었습니다.</p>;
      case "DELETE":
        return <p>아이템이 수정되었습니다.</p>;
      case "EDIT":
        return <p>아이템이 삭제되었습니다.</p>;
      default:
        break;
    }
  };

  return (
    <div>
      <div className="Container">
        <AlertBanner />
        <h2>예산 계산기</h2>
        <article className="BudgetCalculatorForm">
          <BudgetCalculatorForm />
          <DeleteList />
        </article>
        <div className="BudgetTotal">총 지출: 000원</div>
      </div>
    </div>
  );
}

export default FormPage;
