import { useState } from "react";
import { Expenses } from "../Types";

// expenses = [{
//   category : 00,
//   money : 00
// }]

const InputForm = () => {
  const handleCreateClick = (e) => {
    const newExpenses: Expenses = {
      id: Date.now(),
      category: "00",
      money: "00",
    };
    setExpenses([...expenses, newExpenses]);
    return [expenses, "CREATE"];
  };

  return (
    <form className="BudgetInput">
      {/* <article className="BudgetInput" style={{ display: flex }}> */}
      <div className="expensesCategory">
        <span>지출 항목</span>
        <input type="text" />
      </div>
      <div className="expensesMoney">
        <span>비용</span>
        <input type="text" />
      </div>
      <div className="expensesBtn">
        <span> </span>
        <button type="submit" onClick={handleCreateClick}>
          제출
        </button>
      </div>
    </form>
  );
};

export default InputForm;
