import { Expenses, Action } from "../Types";
import FormPage from "../pages/FormPage";

const AlertBanner = ({ key }) => {
  const alertMessage = () => {
    switch (key) {
      case "CREATE":
        return "아이템이 생성되었습니다.";
      case "DELETE":
        return "아이템이 수정되었습니다.";
      case "EDIT":
        return "아이템이 삭제되었습니다.";
      default:
        return;
    }
  };

  return (
    <div className="alertMessage">
      {/* 생성 ㅇㅋㅇㅋ */}
      <p>{alertMessage}</p>
    </div>
  );
};

export default AlertBanner;
