import { useEffect, useState } from "react";

//key가 안 받아와 지는 상태 ㅠㅠ
//key, keyKorean 초기화도 하고, 알람 없으면 아예 없애는 것도.. 어떻게..?
const AlertBanner = ({ keyword }) => {
  const [keyKorean, setKeyKorean] = useState("");

  console.log(1, keyKorean);

  useEffect(() => {
    let timer = setTimeout(() => {
      setKeyKorean(keyword);
    }, 1000);

    return () => {
      clearTimeout(timer);
      setKeyKorean("");
    };
  }, [keyKorean]);

  //fall through: break ^^....
  //^^.... 수정, 삭제를.... 단어를 다르게 넣었네 어쩐지.. ^^.. 왜 오류나나 했네.....^^
  switch (keyword) {
    case "CREATE":
      setKeyKorean("아이템이 생성되었습니다.");
      break;
    case "EDIT":
      setKeyKorean("아이템이 수정되었습니다.");
      break;
    case "DELETE":
      setKeyKorean("아이템이 삭제되었습니다.");
      break;
    default:
      break;
  }

  if (keyKorean === "") {
    return;
  }

  return <div className="alertMessage">{keyKorean}</div>;
};

export default AlertBanner;
