import { useState } from "react";
import { Expenses } from "../Types";

function InputForm({ expenses, handleSubmit }) {
  const [newExpense, setNewExpense] = useState<Expenses[]>(expenses);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newExpenses: Expenses = {
      id: Date.now(),
      category: e.target.value,
      money: e.target.value,
    };
    setNewExpense([...expenses, newExpense]);
    return expenses;
  };

  return (
    <form className="BudgetInput">
      {/* <article className="BudgetInput" style={{ display: flex }}> */}
      <div className="expensesCategory">
        <span>지출 항목</span>
        <input
          className="expensesCategoryValue"
          type="text"
          onChange={handleChange}
        />
      </div>
      <div className="expensesMoney">
        <span>비용</span>
        <input
          className="expensesMoneyValue"
          type="text"
          onChange={handleChange}
        />
      </div>
      <div className="expensesBtn">
        <span> </span>
        <button type="submit">제출</button>
      </div>
    </form>
  );
}

export default InputForm;
