import { useState } from "react";
import { Expenses } from "../Types";

interface Expenses {
  handleSubmit: (e: React.ChangeEvent<HTMLInputElement>) => [Expenses];
}

function InputForm({ handleSubmit, category, money }) {
  const [expenses, setExpenses] = useState<Expenses[]>([]);

  const handleChange = (e) => {
    const newExpenses: Expenses = {
      id: Date.now(),
      category: category,
      money: money,
    };
    setExpenses([...expenses, newExpenses]);
    return expenses;
  };

  return (
    <form className="BudgetInput">
      {/* <article className="BudgetInput" style={{ display: flex }}> */}
      <div className="expensesCategory">
        <span>지출 항목</span>
        <input
          className="expensesCategoryValue"
          type="text"
          onChange={handleChange}
        />
      </div>
      <div className="expensesMoney">
        <span>비용</span>
        <input
          className="expensesMoneyValue"
          type="text"
          onChange={handleChange}
        />
      </div>
      <div className="expensesBtn">
        <span> </span>
        <button type="submit" onClick={handleSubmit}>
          제출
        </button>
      </div>
    </form>
  );
}

export default InputForm;
