import React from "react";
import "./App.css";
// import BudgetCalculatorForm from "./components/BudgetCalculatorForm";

function App() {
  const alertMessage = (key: string) => {
    switch (key) {
      case "CREATE":
        return <p>아이템이 생성되었습니다.</p>;
      case "DELETE":
        return <p>아이템이 수정되었습니다.</p>;
      case "EDIT":
        return <p>아이템이 삭제되었습니다.</p>;
      default:
        break;
    }
  };

  return (
    <div>
      <div className="alertMessage">{/* {alertMessage(key)} */}</div>
      <div className="Container">
        <h2>예산 계산기</h2>
        <article className="BudgetCalculatorForm">
          <article className="BudgetInput">
            {/* <article className="BudgetInput" style={{ display: flex }}> */}
            <div className="expensesCategory">
              <span>지출 항목</span>
              <input type="text" />
            </div>
            <div className="expensesMoney">
              <span>비용</span>
              <input type="text" />
            </div>
            <div className="expensesBtn">
              <span> </span>
              <button>제출</button>
            </div>
          </article>
          <div className="deleteAllBtn">
            <button>목록 지우기</button>
          </div>
          <article className="BudgetLists">
            <ul>
              <li className="BudgetList">
                <span>식비</span>
                <span>10000</span>
                <div>
                  <button></button>
                  <button></button>
                </div>
              </li>
            </ul>
          </article>
        </article>
        <div className="BudgetTotal">총 지출: 000원</div>
      </div>
    </div>
  );
}

export default App;
