import { Expenses, Action } from "../Types";

const AlertBanner = ({ key }) => {
  let keyKorean = "";
  // key = "CREATE";
  console.log(1, keyKorean);
  switch (key) {
    case "CREATE":
      keyKorean = "아이템이 생성되었습니다.";
      console.log(keyKorean);
      break;
    case "DELETE":
      keyKorean = "아이템이 수정되었습니다.";
      console.log(keyKorean);
      break;
    case "EDIT":
      keyKorean = "아이템이 삭제되었습니다.";
      console.log(keyKorean);
      break;
    default:
      break;
  }

  return <div className="alertMessage">{keyKorean}</div>;
};

export default AlertBanner;
