    case "CREATE":
      keyKorean = "아이템이 생성되었습니다.";
      break;
    case "DELETE":
      keyKorean = "아이템이 수정되었습니다.";
      break;
    case "EDIT":
      keyKorean = "아이템이 삭제되었습니다.";
      break;
    default:
      break;
  }

  return (
    <div className="alertMessage">
      {/* 생성 ㅇㅋㅇㅋ */}
      {keyKorean}
    </div>
  );
};

