import { Expenses, Action } from "../Types";
import FormPage from "../pages/FormPage";

const AlertBanner = ({ key }) => {
  const alertMessage = () => {
    switch (key) {
      case "CREATE":
        return <p>아이템이 생성되었습니다.</p>;
      case "DELETE":
        return <p>아이템이 수정되었습니다.</p>;
      case "EDIT":
        return <p>아이템이 삭제되었습니다.</p>;
      default:
        return;
    }
  };

  return (
    <div className="alertMessage">
      {/* 생성 ㅇㅋㅇㅋ */}
      {alertMessage}
    </div>
  );
};

export default AlertBanner;
