import { useEffect } from "react";

//key가 안 받아와 지는 상태 ㅠㅠ
//key, keyKorean 초기화도 하고, 알람 없으면 아예 없애는 것도.. 어떻게..?
const AlertBanner = ({ keyword }) => {
  let keyKorean = "";

  console.log(1, keyKorean);

  useEffect(() => {
    let timer = setTimeout(() => {
      switch (keyword) {
        case "CREATE":
          keyKorean = "아이템이 생성되었습니다.";
          break;
        case "EDIT":
          keyKorean = "아이템이 수정되었습니다.";
          break;
        case "DELETE":
          keyKorean = "아이템이 삭제되었습니다.";
          break;
        default:
          break;
      }
    }, 1000);

    return () => {
      clearTimeout(timer);
      keyKorean = "";
    };
  }, [keyKorean]);

  //fall through: break ^^....
  //^^.... 수정, 삭제를.... 단어를 다르게 넣었네 어쩐지.. ^^.. 왜 오류나나 했네.....^^

  if (keyKorean === "") {
    return;
  }

  return <div className="alertMessage">{keyKorean}</div>;
};

export default AlertBanner;
