import React, { useEffect, useReducer, useState } from "react";
import AlertBanner from "../components/AlertBanner";
import InputForm from "../components/InputForm";
import ExpensesList from "../components/ExpensesList";
import Calculator from "../components/Calculator";
import { Action, Expenses } from "../Types";

function FormPage() {
  let [expenses, setExpenses] = useState<Expenses[]>([]);
  const [categoryValue, setCategoryValue] = useState("");
  const [moneyValue, setMoneyValue] = useState(0);

  const handleSubmit = (e: React.SyntheticEvent) => {
    e.preventDefault();

    const newExpenses: Expenses = {
      id: Date.now(),
      category: e.target["category"].value,
      money: e.target["money"].value,
    };

    // setNewExpense((prev) => [...prev, newExpense]);
    // console.log({ ...newExpense, newExpenses });
    console.log(newExpenses);
    setExpenses([...expenses, newExpenses]);
    console.log(expenses);
  };

  expenses = [
    {
      id: Date.now(),
      category: "아니",
      money: 2000,
    },
    {
      id: Date.now(),
      category: "추가로",
      money: 3000,
    },
    {
      id: Date.now(),
      category: "안나와",
      money: 4000,
    },
  ];

  useEffect(() => {
    // console.log(expenses);
  }, [expenses]);

  return (
    <div>
      <div className="Container">
        <AlertBanner />
        <h2>예산 계산기</h2>
        <article className="BudgetCalculatorForm">
          <form onSubmit={handleSubmit} className="BudgetInput">
            <div className="expensesCategory">
              <label htmlFor="category">지출 항목</label>
              <input
                id="category"
                name="category"
                className="expensesCategoryValue"
                type="text"
              />
            </div>
            <div className="expensesMoney">
              <label htmlFor="money">비용</label>
              <input
                id="money"
                name="money"
                className="expensesMoneyValue"
                type="text"
              />
            </div>
            <div className="expensesBtn">
              <span> </span>
              <button type="submit">제출</button>
            </div>
          </form>
          <ExpensesList />
        </article>
        <div className="BudgetTotal">
          총 지출: <Calculator expenses={expenses} /> 원
        </div>
      </div>
    </div>
  );
}

export default FormPage;
