import { Expenses, Action } from "../Types";

const AlertBanner = (state: Expenses, action: Action) => {
  const alertMessage = (action: Action) => {
    switch (action.type) {
      case "CREATE":
        return <p>아이템이 생성되었습니다.</p>;
      case "DELETE":
        return <p>아이템이 수정되었습니다.</p>;
      case "EDIT":
        return <p>아이템이 삭제되었습니다.</p>;
      default:
        break;
    }
  };
  return (
    <div className="alertMessage">
      생성 ㅇㅋㅇㅋ
      {/* {alertMessage(key)} */}
    </div>
  );
};

export default AlertBanner;
