import React, { useEffect, useReducer, useState } from "react";
import AlertBanner from "../components/AlertBanner";
import InputForm from "../components/InputForm";
import ExpensesList from "../components/ExpensesList";
import { Action, Expenses } from "../Types";

function FormPage() {
  const [expenses, setExpenses] = useState<Expenses[]>([]);
  const [categoryValue, setCategoryValue] = useState("");
  const [moneyValue, setMoneyValue] = useState("");

  const handleSubmit = (e: React.MouseEventHandler<HTMLButtonElement>) => {
    setCategoryValue(handleChange(category));
    setMoneyValue(handleChange(money));
    const newExpenses: Expenses = {
      id: Date.now(),
      category: categoryValue,
      money: moneyValue,
    };
    setExpenses([...expenses, newExpenses]);
  };

  const handleChange = () => {
    const newExpenses: Expenses = {
      id: Date.now(),
      category: category,
      money: money,
    };
    setExpenses([...expenses, newExpenses]);
    return expenses;
  };

  useEffect =
    (() => {
      console.log(expenses);
    },
    [expenses]);

  return (
    <div>
      <div className="Container">
        <AlertBanner />
        <h2>예산 계산기</h2>
        <article className="BudgetCalculatorForm">
          <InputForm
            id={Date.now()}
            handleSubmit={handleSubmit}
            category={categoryValue}
            money={moneyValue}
          />
          <ExpensesList />
        </article>
        <div className="BudgetTotal">총 지출: 000원</div>
      </div>
    </div>
  );
}

export default FormPage;
